DAML.DECORATORS = []
DAML.DecoratorsByType = {}
DAML.DecoratorsByBlock = {}
DAML.DecoratorsByTypeBlock = {}

DAML.addDecorator = function(block_id, type, value, unique) {
  var decorator = { block: block_id
                  , type: type
                  , value: value }
    , existing_decorators
  
  if(unique) {
    existing_decorators = DAML.getDecorators(block_id, type)
    if(existing_decorators && existing_decorators.length) {
      return existing_decorators[0]
    }
  }
  
  if(!DAML.DecoratorsByType[type]) {
    DAML.DecoratorsByType[type] = []
  }
  if(!DAML.DecoratorsByBlock[block_id]) {
    DAML.DecoratorsByBlock[block_id] = []
  }
  if(!DAML.DecoratorsByTypeBlock[type + '-' + block_id]) {
    DAML.DecoratorsByTypeBlock[type + '-' + block_id] = []
  }
  
  DAML.DECORATORS.push(decorator)
  DAML.DecoratorsByType[type].push(decorator)
  DAML.DecoratorsByBlock[block_id].push(decorator)
  DAML.DecoratorsByTypeBlock[type + '-' + block_id].push(decorator)
  
  return decorator
}

DAML.getDecorators = function(by_block, by_type) {
  var decorators = DAML.DECORATORS
  
  if(!by_block) {
    if(by_type) {
      decorators = DAML.DecoratorsByType[by_type]
    }
  }
  else {
    if(by_type) {
      decorators = DAML.DecoratorsByTypeBlock[by_type + '-' + by_block]
    } else {
      decorators = DAML.DecoratorsByBlock[by_block]
    }
  }
  
  return decorators
}


/* GATEWAYS! */

DAML.GATEWAYS = {}
DAML.GATEWAYTYPES = {}

DAML.import_gateway_type = function(name, gtype) {
  if(DAML.GATEWAYTYPES[name])
    return DAML.setError('That gateway type has already been imported')
  
  if(!gtype || !gtype.addListener || !gtype.sendMessage)
    return DAML.setError('That gateway type is missing required properties')
  
  if(typeof gtype.addListener != 'function' || typeof gtype.sendMessage != 'function')
    return DAML.setError("That gateway type's properties are invalid")
  
  DAML.GATEWAYTYPES[name] = gtype
  return true
}

DAML.add_gateway = function(name, type, thing) {
  var gtype = DAML.GATEWAYTYPES[type]
  
  if(!gtype)
    return DAML.setError('Gateway type could not be found')
  
  if(DAML.GATEWAYS[name])
    return DAML.setError('That gateway has already been added')
    
  var gateway = Object.create(gtype)
  gateway.name = name
  gateway.type = type
  gateway.thing = thing
  
  return DAML.GATEWAYS[name] = gateway
}

DAML.import_gateway_type('dom-text', {
  sendMessage: function(value, callback) {
    var element = document.getElementById(this.thing)
    
    if(!element)
      return DAML.setError('That dom thing is not present')
    
    if(!element.hasOwnProperty('innerText'))
      return DAML.setError('That dom thing has no innerText')
    
    element.innerText = DAML.stringify(value)
  },
  addListener: function(callback) {
    // THINK: what should this do? it could put the value on the channel immediately, but that's kind of silly...
  }
})

DAML.import_gateway_type('dom-submit', {
  sendMessage: function(value, callback) {
    var element = document.getElementById(this.thing)
    
    if(!element)
      return DAML.setError('That dom thing is not present')
    
    if(!element.submit)
      return DAML.setError('That dom thing has no innerText')
    
    element.submit()
  },
  addListener: function(callback) {
    var element = document.getElementById(this.thing)
    
    if(!element) 
      return DAML.setError('That dom thing is not present')
    
    if(!element.addEventListener)
      return DAML.setError('That dom thing has no innerText')
    
    var new_cb = function(e) {
      e.preventDefault() // THINK: not sure this is always desired...
      callback()
    }
    
    element.addEventListener('submit', new_cb, false)
    
  }
})

DAML.import_gateway_type('space', {
  sendMessage: function(value, callback) {
    var space = DAML.SPACES[this.thing]
    
    if(!space)
      return DAML.setError('Space could not be found')
    
    // THINK: when would this ever get a real callback?
    if(!callback) {
      callback = function() {} // prevents 'no callback sent' error
    }
    
    space.deliver(value, callback)
  },
  addListener: function(callback) {
    var space = DAML.SPACES[this.thing]
    
    if(!space)
      return DAML.setError('Space could not be found')

    space.listeners.push(callback)
  }
})


