DAML.import_models({
  vars: {},
  ddd: {
    desc: "A helper for doing D3 stuff",
    help: "",
    methods: {

      do: {
        desc: 'Do something',
        help: "",
        params: [
          {
            key: 'action',
            desc: 'Some kind of js function or something',
            type: 'string',
            required: true,
          },
          {
            key: 'params',
            desc: 'An array of parameters, in order',
            type: 'list',
            required: true,
          },
        ],
        fun: function(action, params) {
          // FIXME: this is pretty stupid
          if(typeof window[action] != 'function') return DAML.onerror('That is not a valid action');
          window[action].apply(this, params);
        },
      },

      find: {
        desc: 'Find nouns',
        help: "",
        params: [],
        fun: function() {
          DAML.db.collection('nouns', function(err, c) {
            c.find().toArray(function(err, items) {
              global.output.push(items); /// FIXME!!!!!
            });
          });
        },
      },
    
    }
  }
});
