DAML.import_models({
  vars: {},
  noun: {
    desc: "Nounish stuff",
    help: "",
    methods: {

      find: {
        desc: 'Find nouns',
        help: "",
        params: [],
        fun: function() {
          DAML.db.collection('nouns', function(err, c) {
            c.find().toArray(function(err, items) {
              global.output.push(items); /// FIXME!!!!!
            });
          });
        },
      },

      add: {
        desc: 'Add a noun',
        help: "",
        params: [
          {
            key: 'name',
            desc: 'Some kind of noun name',
            type: 'string',
            required: true,
          },
          {
            key: 'type',
            desc: 'Type of noun',
            type: 'string',
            required: true,
          },
          {
            key: 'data',
            desc: 'Some extra data',
            type: 'hash',
          },
        ],
        fun: function(name, type, data) {
          var noun = {
            name: name,
            type: type,
            data: data,
          };
          DAML.db.collection('nouns', function(err, c) {
            c.insert(noun); // sync-style is ok here, because we're not waiting for confirmation
          });
        },
      },

      set_name: {
        desc: 'Set the name',
        help: "",
        params: [
          {
            key: 'id',
            desc: 'The noun id',
            type: 'string',
            required: true,
          },
          {
            key: 'value',
            desc: 'The value of the new name',
            type: 'string',
            required: true,
          },
        ],
        fun: function(id, value) {
          DAML.db.collection('nouns', function(err, c) {
            c.update({_id: new DAML.mongo.ObjectID(id)}, {$set: {name: value}}, {}, function(err, result) {});
          });
        },
      },

      set_type: {
        desc: 'Set the type',
        help: "",
        params: [
          {
            key: 'id',
            desc: 'The noun id',
            type: 'string',
            required: true,
          },
          {
            key: 'value',
            desc: 'The value of the new type',
            type: 'string',
            required: true,
          },
        ],
        fun: function(id, value) {
          DAML.db.collection('nouns', function(err, c) {
            c.update({_id: new DAML.mongo.ObjectID(id)}, {$set: {type: value}}, {}, function(err, result) {});
          });
        },
      },
    
      set_data: {
        desc: 'Set the data',
        help: "",
        params: [
          {
            key: 'id',
            desc: 'The noun id',
            type: 'string',
            required: true,
          },
          {
            key: 'value',
            desc: 'The value of the new data',
            type: 'hash',
            required: true,
          },
        ],
        fun: function(id, value) {
          DAML.db.collection('nouns', function(err, c) {
            c.update({_id: new DAML.mongo.ObjectID(id)}, {$set: {data: value}}, {}, function(err, result) {});
          });
        },
      },
    
    }
  }
});
